INSERT INTO `study_programs` (`id`, `name`, `code`, `degree_id`, `faculty_id`, `description`, `mission`, `image`, `institution_id`) VALUES
(1, 'Bimbingan Konseling Pendidikan Islam', 'BKPI', 1, 3, NULL, NULL, NULL, 1),
(2, 'Pendidikan Bahasa Arab', 'PBA', 1, 3, '<p>Program Studi Pendidikan Bahasa Arab IAIN Syaikh Abdurrahman Siddik Bangka Belitung memiliki visi untuk menghasilkan tenaga pendidik, peneliti dan pengabdi masyarakat yang unggul, religius dan profesional. Program studi pendidikan bahasa Arab mendapatkan izin operasional berdasarkan surat Keputusan Direktur Jenderal Pendidikan Islam Nomor 3631 tahun 2016 tentang Izin Penyelenggaraan Program Studi pada Program Sarjana Sekolah Tinggi Agama Islam Negeri Syaikh Abdurrahman Siddik Bangka Belitung tanggal 27 Juni 2016. Prodi ini berada di bawah naungan Fakultas Tarbiyah.</p>\r\n\r\n<p>Prodi Pendidikan Bahasa Arab (PBA) sudah mendapatkan akreditasi &ldquo;B&rdquo; sejak tahun 2021. Untuk menyiapkan lulusan yang unggul, Prodi PBA menyediakan berbagai mata kuliah yang sesuai dengan kebutuhan dan perkembangan zaman dan diajarkan oleh dosen yang ahli di bidangnya. Selain itu, Prodi PBA melakukan kerjasama dengan berbagai pihak untuk mendukung terlaksananya pendidikan yang lebih baik.</p>', '<ol>\r\n	<li>Menyelenggarakan Pendidikan Dan Pengajaran Yang Inovatif Berbasis Teknologi Dalam Bidang Pendidikan Bahasa Arab</li>\r\n	<li>Menyelenggarakan Riset Dan Publikasi Ilmiah Dalam Rangka Mengkaji Dan Mengembangkan Keilmuan Di Bidang Pendidikan Bahasa Arab Untuk Dapat Dijadikan Rujukan Masyarakat</li>\r\n	<li>Menyelenggarakan Pengabdian Yang Inovatif Dan Aplikatif Berdasarkan Hasil Pembelajaran Dan Penelitian Di Bidang Pendidikan Bahasa Arab Sehingga Dapat Meningkatkan Kualitas Hidup Masyarakat</li>\r\n	<li>Menjalin Kerjasama Bidang Pendidikan Bahasa Arab Dengan Instansi Dan Lembaga Daerah, Nasional Serta Internasional Untuk Meningkatkan Pendidikan Pembelajaran Dan Mutu Lulusan</li>\r\n</ol>', NULL, 1),
(3, 'Perbankan Syariah', 'PS', 1, 1, '<p>Prodi : <strong>Pe</strong><strong>rbankan Syariah</strong></p>\r\n\r\n<p>Fakultas : Syariah dan Ekonomi Islam</p>\r\n\r\n<p>Perguruan Tinggi : Institut Agama Islam Negeri Syaikh Abdurrahman Siddik Bangka Belitung</p>\r\n\r\n<p>Ketua : Feby Ayu Amalia, M.H</p>\r\n\r\n<p>Sekretaris : Rudi Hartono, M.S.I.</p>\r\n\r\n<p>SK Pendirian : No. ----/D/T/20--</p>\r\n\r\n<p>Tanggal SK Pendirian : -- ---------- 20--</p>\r\n\r\n<p><strong>Nilai Akreditasi</strong> : <strong>Akreditasi </strong><strong>B</strong></p>\r\n\r\n<p><strong>No SK&nbsp;</strong> <strong>LAMEMBA</strong> :&nbsp; 107/DE/A.5/AR.10/XII/2022</p>', '<p>1. Menyelenggarakan pendidikan dan pengajaran yang berkualitas untuk melahirkan sumber daya manusia yang berkualitas di bidang perbankan syariah secara akademik dan profesional serta memiliki pribadi yang Islami</p>\r\n\r\n<p>2. Melakukan penelitian dan kajian dibidang perbankan syariah untuk mengembangkan ilmu pengetahuan, ekonomi dengan menggunakan paradigma Islam.</p>\r\n\r\n<p>3. Menerapkan ilmu pengetahuan, ekonomi sebagai bentuk pengabdian kepada masyarakat dengan menggunakan paradigma Islam.</p>', NULL, 1),
(4, 'Pendidikan Islam Anak Usia Dini', 'PIAUD', 1, 3, NULL, '<ol>\r\n	<li>Menyelenggarakan pendidikan dan pengajaran&nbsp; dalam bidang pendidikan Islam Anak usia dini yang Profesional, Agamis, Cendekia dan&nbsp; Unggul berbasis teknologi informasi.</li>\r\n	<li>Menyelenggarakan penelitian&nbsp; dalam bidang pendidikan Islam Anak usia dini yang Profesional, Agamis, Cendekia dan&nbsp; Unggul berbasis teknologi informasi.</li>\r\n	<li>Menyelenggarakan pengabdian dalam bidang pendidikan Islam Anak usia dini yang Profesional, Agamis, Cendekia dan &nbsp;Unggul berbasis teknologi informasi.</li>\r\n	<li>Menyelenggarakan kerjasama dalam bidang pendidikan Islam Anak usia dini yang Unggul, Cendekia, Agamis dan Profesioonal berbasis teknologi informasi.</li>\r\n</ol>', 'WhatsApp Image 2023-10-19 at 11.13.05.jpeg', 1),
(5, 'Tadris Bahasa Inggris', 'TBI', 1, 3, NULL, NULL, NULL, 1),
(6, 'Pendidikan Agama Islam', 'PAI', 1, 3, NULL, NULL, NULL, 1),
(7, 'Hukum Keluarga Islam', 'HKI', 1, 1, '<p>Program Studi Hukum Keluarga Islam (Ahwal Syakhshiyyah) sebagai salah satu program studi di Fakultas Syari&rsquo;ah dan Ekonomi Islam berdasarkan Keputusan Direktur Jenderal Pendidikan Islam No. 3631 Tahun 2016, menjembatani tuntutan perkembangan jaman tersebut dengan gagasan keilmuan di bidang Hukum Islam, selaras dengan tuntutan perkembangan zaman, pengembangan ilmu pengetahuan dan problematika masyarakat modern saat ini dan yang akan datang. Program Studi ini adalah jalur pendidikan dan pengembangan ilmu hukum Islam yang aplikatif dan sangat strategis untuk memenuhi pembangunan bidang hukum, khususnya sektor Hukum Perdata Islam. Hadirnya Program Studi Hukum Keluarga Islam (Ahwal Syakhshiyyah) bersamaan dengan semakin banyaknya Kantor Urusan Agama, Pengadilan Agama, dan lembaga hukum lainnya di kabupaten dan Kota yang ada di setiap provinsi yang ada di Indonesia. Program studi ini diharapkan mampu mengakomodasi kebutuhan penanganan problematika kehidupan, khususnya di bidang Hukum Islam. Melihat kenyataan ini, peluang besar bagi terserapnya para alumni di berbagai instansi yang terkait dengan hukum menjadi sebuah keniscayaan.&nbsp;</p>', '<ol>\r\n	<li>Menyiapkan sumber daya manusia memiliki kualifikasi di bidang Hukum Keluarga Islam (Ahwal Syakhshiyyah)</li>\r\n	<li>Mengembangkan sistem hukum Islam sebagai basis hukum keluarga Islam.</li>\r\n	<li>Menyebarkan ide, melakukan aksi, sinergi, advokasi dan edukasi di bidang hukum keluarga Islam.</li>\r\n</ol>', NULL, 1),
(8, 'Magister Pendidikan Agama Islam', 'MPAI', 4, 1, '<p><strong>PROFIL PROGRAM STUDI PENDIDIKAN AGAMA ISLAM</strong></p>', '<h3><strong>Misi</strong></h3>\r\n\r\n<ol>\r\n	<li>Menyelenggarakan Pendidikan dan Pengajaran yang memiliki kompetensi keilmuan yang unggul, kompetitif, religius, dan profesional&nbsp;</li>\r\n	<li>Meyelnggarakn serta meningkatkan kualitas dan kuantitas riset dan publkasi ilmiah dalam bidang pedidikan agama islam (PAI) yang berdaya saing di masyarakat</li>\r\n	<li>Menghasilkan pengabdian kepada masyarakat yang kebih bersifat kereatif, inovatif, dan aplikatif</li>\r\n	<li>Menyelenggarakan dan mengembangkan tata kelolo perguruan tinggi yang baik (good university governance), dan korporasi yang baik (good corporate governance), transparan dan akuntabel.&nbsp;</li>\r\n</ol>', NULL, 1),
(9, 'Magister Ekonomi Syariah', 'MES', 4, 1, '<p><strong>PROFIL PROGRAM STUDI EKONOMI SYARIAH</strong></p>', '<h3><strong>Misi</strong></h3>\r\n\r\n<ol>\r\n	<li>Menyelenggarakan sistem pendidikan Magister&nbsp;Ekonomi Syariah secara Multidisipliner dalam bidang ekonomi dan keuangan Syariah.&nbsp;</li>\r\n	<li>Menyelengg arakan&nbsp;riset untuk menghasilkan karya ilmiah yang inovatif dalam bidang ekonomi dan keuangan syariah.</li>\r\n	<li>Menghasilkan master ekonomi dan keuangan syariah yang agamis, berpengetahuan dan responsif serta mampu bersaing di tingkat nasional.</li>\r\n	<li>Melakukan sinergi dengan perguruan tinggi, lembaga, dan&nbsp;<em>stakeholder</em>&nbsp;lainnya untuk memajukan ekonomi dan keuangan syariah.&nbsp;</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>', NULL, 1),
(10, 'Akutansi Syariah', 'AKS', 1, 1, NULL, NULL, NULL, 1),
(11, 'Hukum Ekonomi Syariah', 'HES', 1, 1, '<p>Program Studi Hukum Ekonomi Syariah (disingkat HES) merupakan salahsatu Program Studi di bawah Fakultas Syariah dan Ekonomi Islam IAIN Syaikh Abdurrahman Siddik Bangka Belitung. Prodi HES secara resmi berdiri pada 8 Desember 2021 berdasarkan SK Menteri Agama Republik Indonesia No. 1324 Tahun 2021. Prodi HES bertujuan melahirkan Sarjana yang menguasai ilmu di Bidang Hukum Ekonomi Syariah (Hukum Perdata dan Bisnis Islam). Lulusan dari Prodi HES mendapatkan gelar S,H (Sarjana Hukum) dan dapat berkiprah sebagai sebagai Praktisi Hukum (Hakim, Advokad, Mediator, dll), Pengawas Syariah dan Peniliti di bidang Hukum Ekonomi Syariah.&nbsp;</p>', '<ol>\r\n	<li>Menyelenggarakan pendidikan dan pengajaran di bidang Hukum Ekonomi syariah yang unggul, religius, dan professional;</li>\r\n	<li>Menyelenggarakan penelitian dan pengembangan keilmuan di bidang Hukum Ekonomi syariah yang unggul, religius, dan professional;</li>\r\n	<li>Menyelengarakan pengabdian pada masyarakat di bidang Hukum Ekonomi syariah yang unggul, religius, dan professional;</li>\r\n	<li>Menjalin kerjasama dengan pengadilan agama, Lembaga keuangan syariah, pemerintah daerah dan stakeholder lainnya</li>\r\n</ol>', NULL, 1),
(12, 'Pariwisata Syariah', 'PARSY', 1, 1, '<p><strong>Pariwisata Syariah, Fakultas&nbsp;Syariah dan Ekonomi Islam,&nbsp;IAIN Syaikh Abdurrahman Siddik Bangka Belitung</strong></p>', '<ol>\r\n	<li>Menyelenggarakan pendidikan dan pengajaran berbasis teknologi informasi dan komunikasi di bidang pariwisata syariah yang unggul, religius, dan professional;</li>\r\n	<li>Menyelanggarakan penelitian dan pengembangan keilmuan di bidang pariwisata syariah yang unggul, religius, dan professional;</li>\r\n	<li>Menyelengarakan pengabdian pada masyarakat di bidang pariwisata syariah yang unggul, religius, dan professional;</li>\r\n	<li>Menjalin kerjasama dengan lembaga terkait pengelolaan pariwisata, pariwisata syariah, dan stakeholder lainnya</li>\r\n</ol>', NULL, 1),
(13, 'Komunikasi Penyiaran Islam', 'KPI', 1, 4, '<p>Program Studi Komunikasi dan Penyiaran Islam (KPI) IAIN Syaikh Abdurrahman Siddik Bangka Belitung merupakan prodi baru yang secara resmi berdiri pada tahun 2014 melalui SK pendirian PS No. 1486 tahun 2014 tepatnya pada tanggal 14 Maret 2014. Pada saat itu pula Program Studi KPI IAIN SAS Bangka Belitung resmi melakukan penyelenggaraan pendidikan tinggi yang bertujuan untuk menciptakan tenaga komunikasi yang rofesional dalam merespon kebutuhan lembaga-lembaga dan masyarakat terutama dibidang komunikasi dan penyiaran.&nbsp;</p>\r\n\r\n<p>Ditinjau dari aspek historis, berdirinya Program Studi KPI IAIN SAS Bangka Belitung tidak terpisah dari terbentuknya Fakultas Dakwah dan Komunikasi Islam IAIN SAS Bangka Belitung. Lahirnya institusi ini merupakan metamorphosis perubahan bentuk dari Sekolah Tinggi Agama Islam Negeri Syaikh Abdurrahman Siddik (STAIN SAS) Bangka Belitung menjadi IAIN SAS Bangka Belitung. Secara Yuridis formal perubahan bentuk tersebut berdasarkan Peraturan Presiden Nomor 30 Tahun 2018 yang selanjutnya diundangundangkan di Kementerian Hukum dan HAM pada tanggal 7 April 2018.</p>\r\n\r\n<p>Sepanjang perjalanannya, STAIN SAS Bangka Belitung sudah beberapa kali berganti pucuk pimpinan (Ketua). STAIN SAS Bangka Belitung pertama kali dipimpin oleh Drs. Janawi, M.Ag yang ditunjuk menjadi Pejabat Sementara (Pjs.). Selanjutnya, berdasarkan hasil pemilihan, Drs. Buswatin Abdullah di-mandatkan memimpin STAIN SAS Bangka Belitung untuk Periode 2005 &ndash; 2009. Dalam perjalanannya, pada bulan Juni 2007, Drs. Buswatin Abdullah meninggal dunia akibat sakit, jabatan ketua digantikan oleh Drs. Zulkifli, MA sebagai pengganti sementara. Sesuai dengan tugasnya selaku Pgs., meskipun sempat tertunda beberapa lama, proses penjaringan dan pemilihan ketua definitif berlangsung sejak akhir 2009. Setelah melalui proses panjang dan &ldquo;mene-gangkan&rdquo;, terpilihlah Prof. Dr. H. Imam Malik, M.Ag sebagai ketua definitif periode 2010 &ndash; 2014, namun di tengah perjalanan (tahun 2013), dibebastugaskan dari jabatan ketua oleh Menteri Agama dan digantikan oleh Dr. Zayadi, M.Ag. Selanjutnya, Dr. Zayadi, M.Ag selaku pejabat pengganti sementara (Pgs.) kemudian terpilih menjadi calon Ketua STAIN SAS Bangka Belitung definitif untuk periode 2014 - 2018 setelah berakhirnya periode jabatan ketua definit sebelumnya. Meskipun demikian, hingga saat ini, hasil pemilihan yang dilaporkan ke Pusat tersebut belum ditindaklanjuti sehingga pucuk pimpinan STAIN SAS Bangka Belitung masih dipegang oleh Pgs. Ketua. Setelah Ketua Definitif (Dr. Zayadi, M.Ag) dilantik menjadi Ketua STAIN SAS Bangka Belitung pada tahun 2014, ide-ide perubahan bentuk mulai dilakukan. Akhir dari proses perjuangan tersebut, terbitlah Perpres Nomor 30 Tahun 2018 tentang Perubahan bentuk STAIN SAS Bangka Belitung menjadi IAIN SAS Bangka Belitung. Dalam runtutan sejarah tersebut akhirnya ikut merubah kelembagaan Jurusan Dakwah STAIN SAS Bangka Belitung</p>\r\n\r\n<p>menjadi Fakultas Dakwah dan Komunikasi Islam IAIN SAS Bangka Belitung, yang dikukuhkan oleh Rektor IAIN SAS Bangka Belitung melalui pelantikan Dr. Zaprulkhan, M.SI menjadi Dekan Fakultas Dakwah dan Komunikasi IAIN SAS Bangka Belitung, berdasarkan Surat Keputusan Rektor (SK) Nomor: 439/In.40/Kp.07.6/06/03/2019 tanggal 15 Maret 2019.</p>\r\n\r\n<p>Berdasarkan transformasi kelembagaan itulah Prodi KPI IAIN SAS Bangka Belitung terus berupaya untuk menjaga eksistensi kelembagaannya. Sejak berdirinya program studi KPI IAIN SAS Bangka Belitung selalu mengadakan pembenahan-embenahan dalam berbagai bidang, baik di bidang peningkatan mutu dosen, karyawan, bidang sarana dan prasarana seperti: Fasilitas ruang perkuliahan, perkantoran, perpustakaan, laboratorium, serta<br />\r\nsarana pembinaan kemahasiswaan. Sedangkan dalam bidang kurikulum, evaluasi kinerja dosen, serta pendalaman dan pemantapan kemampuan mahasiswa sesuai dengan&nbsp;<br />\r\nkompetensinya dalam laboratorium dan kerjasama-kerjasama untuk peningkatan dan pengembangan prodi KPI, penambahan bahan pustaka, serta meningkatkan kegiatan mahasiswa baik ko-kurikuler, intra-kurikuler maupun ekstra-kurikuler.</p>', '<p>Misi Prodi KPI IAIN SAS Bangka Belitung;</p>\r\n\r\n<ol>\r\n	<li>Menghasilkan lulusan yang unggul di bidang komunikasi dan penyiaran Islam, berkarakter Islami, dan memiliki jiwa Nasionalisme;</li>\r\n	<li>Meningkatkan penyelenggaraan tridharma perguruan tinggi yang mengedepankan mutu pelayanan;</li>\r\n	<li>Menyelenggaraan dan meningkatkan kualitas dan kuantitas penelitian dan publikasi ilmiah dalam bidang Komunikasi dan Penyiaran Islam atas dasar kemajuan IPTEKS dan tanggung jawab sosial;</li>\r\n	<li>Menyelenggarakan dan meningatkan kualitas dan kuantitas pengabdian kepada masyarakat dan layanan publik dalam bidang komunikasi dan penyiaran Islam secara profesional dan novatif dalam rangka mewujudkan masyarakat yang mandiri, berkualitas, dan bermartabat;</li>\r\n	<li>&nbsp;Mengembangkan tata kelola program studi yang baik, berkelanjutan, dan berkeadilan.</li>\r\n</ol>', NULL, 1),
(14, 'Jurnalistik Islam', 'JI', 1, 4, NULL, '<p>Misi program studi Jurnalistik Islam sebagai berikut :<br />\r\n1. Melaksanakan program pendidikan yang unggul dibidang Jurnalistik, berbasis teknologi informasi dan komunikasi (TIK) agar Lulusan memiliki keunggulan kompetitif (competitiveness advantages).<br />\r\n2. Mewujudkan sumberdaya yang unggul, religius, intelektual, dan professional<br />\r\n3. Melaksanakan penelitian dan pengabdian kepada masyarakat yang kolaboratif dan komprehensif di bidang Jurnalistik.<br />\r\n4. Memperluas kerjasama dalam berbagai bidang yang berkontribusi pada pemecahan masalah sosial dan keagamaa</p>', 'JI .jpg', 1),
(15, 'Psikologi Islam', 'PI', 1, 4, '<p>Program Studi Psikologi Islam adalah salah satu program studi yang berada di Fakultas Dakwah dan Komunikasi Islam. Prodi Psikologi Islam tidak hanya fokus pada dimensi psikologis saja, tetapi juga dimensi religius dengan pendekatan bercorak interdisipliner. Psikologi Islam juga mengembangkan dimensi praktis lainnya seperti, kreativitas, pembentukkan karakter (character building), inovatif, diagnostik, dan problem solving.</p>', '<ol>\r\n	<li>Membentuk lulusan yang mampu melakukan integrasi dan interkoneksi keilmuan psikologi dengan pemahaman kajian keislaman.</li>\r\n	<li>Menyelenggarakan pendidikan psikologi islam yang fokus pada aspek psikologis dan religius sebagai pendekatan dalam menganalisis gejala sosial.</li>\r\n	<li>Menyelenggarakan penelitian di bidang psikologi islam berlandaskan pada konsep psikologi dan kajian keislaman.</li>\r\n	<li>Menyelenggarakan pengabdian kepada masyarakat di bidang psikologi islam.</li>\r\n	<li>Menyelenggarakan kerja sama dengan instansi/lembaga di bidang psikologi islam.</li>\r\n</ol>', 'Logo Psychosophia.png', 1),
(16, 'Bimbingan Konseling Islam', 'BKI', 1, 4, '<p>Program Studi Bimbingan dan Konseling Islam (Prodi BKI) merupakan program studi pertama pada Fakultas Dakwah dan Komunikasi Islam IAIN Syaikh Abdurrahman Siddik Bangka Belitung. Prodi BKI telah terakreditasi (B) pada Tanggal 20 Desember 2016.</p>\r\n\r\n<p>Dalam perjalanannya, pada tahun 2012, terjadi perubahan nomenklatur pada beberapa program studi di STAIN SAS BABEL yang saat ini sudah beralih status menjadi IAIN SAS BABEL. Salah satu Prodi yang mengalami perubahan nomenklatur adalah Pogram Studi Bimbingan dan Penyuluhan Islam (BPI) menjadi Program Studi Bimbingan dan Konseling Islam (BKI). Sejak awal beroperasi, Prodi BKI mendasarkan pada:</p>\r\n\r\n<ol>\r\n	<li>Undang-Undang Nomor 61 Tahun 1999 tentang Otonomi Perguruan Tinggi Negeri: Kampus diberi kebebasan seluas-luasnya untuk mengembangkan usaha sendiri.</li>\r\n	<li>Peraturan Pemerintah Nomor 30 Tahun 1990:</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Pasal 1 ayat 4: Pendidikan profesional adalah pendidikan tinggi yang diarahkan terutama kesiapan penerapan keahlian tertentu.</li>\r\n	<li>Pasal 2 ayat 1, tujuan pendidikan tinggi, butir 1 dan 2.</li>\r\n	<li>Menyiapkan peserta didik menjadi anggota masyarakat yang memiliki kemampuan akademik dan/atau profesional yang dapat menerapkan, mengembangkan, dan/atau menciptakan ilmu pengetahuan, teknologi dan/atau kesenian.</li>\r\n	<li>Mengembangkan dan menyebarluaskan ilmu pengetahuan, teknologi, dan kesenian serta mengupayakan penggunaannya untuk meningkatkan taraf kehidupan masyarakat dan memperkaya kebudayaan nasional.</li>\r\n	<li>Pasal 4 ayat 2: Pendidikan akademik mengutamakan peningkatan mutu dan memperluas wawasan ilmu pengetahuan dan diselenggarakan oleh sekolah tinggi, institut, dan universitas.</li>\r\n	<li>Pasal 4 ayat 3: Pendidikan profesional mengutamakan peningkatan kemampuan penerapan ilmu pengetahuan dan diselenggarakan oleh sekolah tinggi, institut, dan universitas.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li>Keppres Nomor 93 Tahun 2004 tentang Perubahan status STAI YPIB dan Pendirian STAIN Syaikh Abdurrahman Siddik Babel.</li>\r\n	<li>KMA Nomor 03 Tahun 2005 tentang Tata Laksana dan Struktur STAIN Syaikh Abdurrahman Siddik. Dalam KMA dijelaskan bahwa STAIN Syaikh Abdurrahman Siddik memiliki 3 Jurusan: Tarbiyah, Dakwah, dan Syari&rsquo;ah.</li>\r\n</ol>\r\n\r\n<p>Lebih lanjut, dibukanya Prodi BKI ini dilandasi oleh berbagai argumentasi yang secara spesifik, yaitu:</p>\r\n\r\n<ol>\r\n	<li>Prodi BKI adalah jalur pendidikan dan pengembangan ilmu agama yang aplikatif dan sangat strategis untuk memenuhi pembangunan bidang dakwah, khususnya pada aspek BKI. Pesatnya pembangunan di Bangka Belitung seiring tuntutan perkembangan dan kebutuhan sebagai propinsi baru dan menjawab tantangan pengembangan dan aplikasi ilmu pengetahuan, teknologi dalam&nbsp; perspektif masyarakat madani merupakan potensi sekaligus peluang yang harus&nbsp; direspons secara konstruktif oleh Prodi BKI.</li>\r\n	<li>Seiring perubahan status Bangka Belitung menjadi sebuah provinsi baru, terjadi pemekaran wilayah yang diikuti oleh maraknya pendirian institusi diwilayah tersebut, baik itu institusi yang didirikan oleh pemerintah mapun institusi mandiri yang melayani kebutuhan masyarakat.seperti institusi Rumah sakit, sekolah, panti rehabilitasi, klinik, lembaga keagamaan, persusahaan-perusahaan dan lainnya. Maraknya pendirian institusi tersebut tentunya diikuti pula dengan kebutuhan untuk merekrut tenaga profesional antara lain di bidang BKI. Prospek ini diyakini sebagai salah satu peluang yang menjanjikan bagi lulusan Prodi BKI.</li>\r\n	<li>Tidak bisa dimungkiri bahwa banyaknya lulusan Madrasah Aliyah ataupun SMA cenderung memilih kuliah ke Perguruan Tinggi Umum karena terlihat lebih prospektif, <em>marketable,</em> dan menjanjikan lapangan pekerjaan yang lebih luas. Padahal, baik lulusan MA maupun SMA, juga merupakan <em>basic source</em><em>s </em>atau calon mahasiswa bagi Perguruan Tinggi Keagamaan Islam Negeri (PTKIN). Oleh karena itu, diperlukan kratifitas dan inovasi antara lain dengan membuka program studi baru di lingkungan PTKIN, khususnya IAIN SAS BABEL agar lebih diminati oleh lulusan sekolah menengah atau sederajat.</li>\r\n	<li>Pada tingkat aplikasinya, BKI merupakan bagian integral dari aktivitas dakwah. Fakultas Dakwah dan Komunikasi Islam pun menjadi jalur pendidikan dan pengembangan ilmu agama yang aplikatif dan sangat strategis untuk memenuhi kebutuhan umat. Oleh karenanya, dalam rangka memperkuat pelaksanaan dakwah islamiyah, perlu dilakukan terobosan melalui penerapan layanan BKI. Dengan demikian, diperlukan akselerasi ketersediaan tenaga akademik maupun tenaga profesional bidang BKI.</li>\r\n	<li>Fakultas Dakwah dan Komunikasi Islam memiliki khasanah keilmuan dan keterampilan yang variatif sejalan dengan perkembangan dan kebutuhan masyarakat Bangka Belitung.</li>\r\n</ol>\r\n\r\n<p>Kehadiran IAIN SAS BABEL sebagai Perguruan Tinggi Agama Islam Negeri pertama di Provinsi Kepulauan Bangka Belitung merupakan suatu berkah yang patut disyukuri. Begitu pula dengan Prodi BKI, yang juga pertama dan satu-satunya ini, diharapkan bisa menyiapkan calon sarjana BKI yang andal dan profesional di bidangnya. Kehadiran Prodi BKI ini diharapkan juga dapat membantu pemerintah dalam rangka pembangunan masyarakat, khususnya di Bangka Belitung.</p>\r\n\r\n<p>Prodi BKI didirikan dan diselenggarakan dalam rangka mencerdaskan dan meningkatkan kesejahteraan masyarakat-bangsa Indonesia, khususnya dalam bidang keilmuan BKI, sesuai dengan Pancasila, UUD 1945, serta Undang-Undang Nomor 20 Tahun 2003 tentang Sistem Pendidikan Nasional sebagai dasar hukum penyelenggaraan dan reformasi sistem pendidikan nasional. Dengan demikian, kejujuran, keterbukaan, dan kepedulian terhadap masyarakat harus terkandung dalam konsep dan pelaksanaan pendidikannya. Hal ini ditegaskan dalam visi, misi, tujuan, dan sasaran strategis IAIN SAS BABEL.</p>\r\n\r\n<p>Tujuan pendirian Prodi BKI pada Fakultas Dakwah dan Komunikasi Islam IAIN Syaikh Abdurrahman Siddik Bangka Belitung, di samping sebagai dakwah &lsquo;<em>amar ma&rsquo;ruf nahi munkar</em>, juga untuk mencerdaskan kehidupan masyarakat melalui pendidikan yang merata. Untuk merealisasikan capaian tersebut, kajian terhadap visi, misi, dan tujuan Prodi BKI melibatkan berbagai unsur internal prodi, fakultas, dan institusi, instansi pemerintah daerah (provinsi/kabupaten/kota), tokoh agama, tokoh masyarakat, serta masyarakat di sekitar kampus IAIN Syaikh Abdurrahman Siddik Bangka Belitung. Sinergi tersebut menunjukkan bahwa Prodi BKI merupakan prodi yang inklusif dalam menegakkan <em>amar ma&rsquo;ruf nahi munkar</em>. Tujuan, sasaran, dan strategi pencapaian yang mendukung tercapainya visi dibagi ke dalam beberapa bidang, yakni: 1) Pendidikan; 2) Penelitian dan Pengabdian kepada Masyarakat; serta 3) Pengembangan ilmu dan kebudayaan.</p>', '<ol>\r\n	<li>Menyelenggarakan pendidikan dan pengajaran Bimbingan dan Konseling Islam yang unggul, religius, intelektual dan Profesional berbasis teknologi informasi</li>\r\n	<li>Menyelenggarakan penelitian dalam bidang Bimbingan dan Konseling Islam yang unggul, religius, intelektual dan Profesional &nbsp;&nbsp;&nbsp;</li>\r\n	<li>Menyelenggarakan pengabdian dalam bidang Bimbingan dan Konseling Islam yang unggul, religius, intelektual dan Profesional berbasis teknologi informasi</li>\r\n	<li>Menyelenggarakan kerjasama dalam bidang Bimbingan dan Konseling Islam yang unggul, religius, intelektual dan Profesional</li>\r\n</ol>', NULL, 1);
COMMIT;

